mapper:






















    select  rest.id, rest.name, rest.phoneNum, rest.location, rest.createDate, rest.restaurantImg,rest.state
     u.id as user_id, u.name as user_name, u.phoneNum as user_phoneNum, u.password from restaurant rest,user u where u.id=rest.owner and rest.id=#{id}



    select* from restaurant where phoneNum=#{phoneNum}




    SELECT * from restaurant


增加user<-->

    INSERT  into restaurant(name,owner,phoneNum,location,createdDate,restaurantImg,state)values(#{name},#{owner},#{phoneNum},#{location},#{createdDate},#{restaurantImg},#{state})


删除restaur<-->

    DELETE  from restaurant where id=#{id}


修改restaurant属性<-->

    update restaurant set name=#{name},owner=#{owner},phoneNum=#{phoneNum},location=#{location},createdDate=#{createdDate},restaurantImg=#{restaurantImg},state=#{state} where id=#{id}
